import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Expense {
  _id?: string;
  title: string;
  amount: number;
  category?: string;
  date?: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  expenses: Expense[] = [];
  newExpense: Expense = { title: '', amount: 0, category: '' };
  apiUrl = 'http://localhost:4000/api/expenses';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.getExpenses();
  }

  getExpenses() {
    this.http.get<Expense[]>(this.apiUrl).subscribe((data) => {
      this.expenses = data;
    });
  }

  addExpense() {
    if (!this.newExpense.title || !this.newExpense.amount) return;
    this.http.post<Expense>(this.apiUrl, this.newExpense).subscribe(() => {
      this.newExpense = { title: '', amount: 0, category: '' };
      this.getExpenses();
    });
  }

  deleteExpense(id?: string) {
    if (!id) return;
    this.http.delete(`${this.apiUrl}/${id}`).subscribe(() => {
      this.getExpenses();
    });
  }
}
<div class="container">
  <h2>üí∞ Expense Tracker</h2>

  <div class="form">
    <input [(ngModel)]="newExpense.title" placeholder="Title" />
    <input [(ngModel)]="newExpense.amount" type="number" placeholder="Amount" />
    <input [(ngModel)]="newExpense.category" placeholder="Category" />
    <button (click)="addExpense()">Add Expense</button>
  </div>

  <ul>
    <li *ngFor="let e of expenses">
      <strong>{{ e.title }}</strong> - ‚Çπ{{ e.amount }}
      <small *ngIf="e.category">({{ e.category }})</small>
      <button (click)="deleteExpense(e._id)">‚ùå</button>
    </li>
  </ul>
</div>
