import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';

interface Expense {
  _id?: string;
  title: string;
  amount: number;
  category?: string;
  date?: string;
}

@Component({
  selector: 'app-root',
  standalone: true,
  imports: [],
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {
  expenses: Expense[] = [];
  newExpense: Expense = { title: '', amount: 0, category: '', date: '' };

  expenseType: string = 'personal'; // ‚úÖ default selection
  categories: string[] = [];        // ‚úÖ will change dynamically

  apiUrl = 'http://localhost:4000/api/expenses';

  constructor(private http: HttpClient) {}

  ngOnInit(): void {
    this.updateCategories();
    this.getExpenses();
  }

  updateCategories() {
    if (this.expenseType === 'personal') {
      this.categories = ['Housing', 'Food', 'Transportation', 'Utilities'];
    } else {
      this.categories = ['Salaries', 'Rent', 'Marketing', 'Supplies'];
    }
    // Clear category when type changes
    this.newExpense.category = '';
  }

  getExpenses() {
    this.http.get<Expense[]>(this.apiUrl).subscribe((data) => {
      this.expenses = data;
    });
  }

  addExpense() {
    if (!this.newExpense.title || !this.newExpense.amount) return;
    this.http.post<Expense>(this.apiUrl, this.newExpense).subscribe(() => {
      this.newExpense = { title: '', amount: 0, category: '', date: '' };
      this.getExpenses();
    });
  }

  deleteExpense(id?: string) {
    if (!id) return;
    this.http.delete(`${this.apiUrl}/${id}`).subscribe(() => {
      this.getExpenses();
    });
  }
}
<div class="container">
  <h2>üí∞ Expense Tracker</h2>

  <!-- Expense Form -->
  <div class="form">
    <input [(ngModel)]="newExpense.title" placeholder="Title" />
    <input [(ngModel)]="newExpense.amount" type="number" placeholder="Amount (‚Çπ)" />
    <input [(ngModel)]="newExpense.date" type="date" placeholder="Date" />

    <!-- Type Selection -->
    <select [(ngModel)]="expenseType" (change)="updateCategories()">
      <option value="personal">Personal</option>
      <option value="business">Business</option>
    </select>

    <!-- Category Selection -->
    <select [(ngModel)]="newExpense.category">
      <option value="" disabled selected>Choose Category</option>
      <option *ngFor="let cat of categories" [value]="cat">{{ cat }}</option>
    </select>

    <button (click)="addExpense()">‚ûï Add Expense</button>
  </div>

  <!-- Expense List -->
  <ul class="expense-list">
    <li *ngFor="let e of expenses" class="expense-item">
      <div class="expense-info">
        <h3>{{ e.title }}</h3>
        <p class="amount">‚Çπ{{ e.amount }}</p>
        <small *ngIf="e.category">üìÇ {{ e.category }}</small>
        <small *ngIf="e.date">üìÖ {{ e.date | date: 'mediumDate' }}</small>
      </div>
      <button (click)="deleteExpense(e._id)" class="delete-btn">üóëÔ∏è</button>
    </li>
  </ul>
</div>
